import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  beforeRouteUpdate(to, from, next) {
    this.message = to.params.message;
    this.selectAll();
    next();
  },
  data() {
    return {
      message: this.$route.params.message,
      users: [],
      headers: [{
        text: 'ユーザ名',
        value: 'userName'
      }, {
        text: 'SlackID',
        value: 'slackId'
      }, {
        text: '操作',
        value: 'delete',
        align: 'center',
        sortable: false
      }]
    };
  },
  created() {
    this.selectAll();
  },
  methods: {
    selectAll() {
      const path = 'http://localhost:8080/user/selectUserAll';
      axios.get(path).then(response => {
        this.users = response.data;
      }).catch(error => {
        console.log(error);
      });
    },
    insert() {
      this.$router.push("MemberInsert");
    },
    del(id) {
      confirm("削除してもよろしいですか？");
      const path = 'http://localhost:8080/user/deleteUser?id=' + id;
      axios.delete(path).then(response => {
        console.log(response.data);
        this.$router.go({
          name: "MemberManagement",
          params: {
            message: "DeleteComplete!!"
          }
        });
      }).catch(err => {
        console.log("err:", err);
      });
    },
    update(id, userName, slackId) {
      this.$router.push({
        name: "MemberUpdate",
        params: {
          id: id,
          userName: userName,
          slackId: slackId
        }
      });
    }
  }
};
